CREATE TABLE custom_field_data (\n    id INT PRIMARY KEY,\n    entity_id INT,          -- ID of the entity (e.g., user_id)\n    field_definition_id INT,-- References custom_field_definitions(id)\n    value TEXT,               -- Value of the custom field\n    FOREIGN KEY (entity_id) REFERENCES users(id),\n    FOREIGN KEY (field_definition_id) REFERENCES custom_field_definitions(id)\n);