CREATE TABLE `sessions` (\n  `session_id` CHAR(32) NOT NULL PRIMARY KEY COMMENT 'Session ID (hexadecimal)',\n  `user_id` INT UNSIGNED NOT NULL COMMENT 'User ID of the user owning this session',\n  `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'When was the session created?',\n  `last_activity` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Time of last activity in the session',\n  `ip_address` VARCHAR(45) NOT NULL COMMENT 'IP address from which the user logged in',\n  `user_agent` TEXT NOT NULL COMMENT 'User-Agent string of the user'\n);