DECLARE\n     v_ddl CLOB;\n     v_file UTL_FILE.FILE_TYPE;\n     v_line VARCHAR2(32767);\n     CURSOR c_objects IS\n       SELECT object_name\n       FROM user_objects\n       WHERE object_type IN ('PROCEDURE', 'FUNCTION', 'PACKAGE')\n         AND object_name = 'YOUR_OBJECT_NAME'; -- Change this line to filter specific objects or remove the condition to process all\n   BEGIN\n     FOR rec IN c_objects LOOP\n       v_ddl := DBMS_METADATA.GET_DDL('OBJECT_TYPE', rec.object_name);\n       v_file := UTL_FILE.FOPEN('EXPORT_DIR', rec.object_name || '.sql', 'W');\n       \n       FOR i IN 1 .. CEIL(DBMS_LOB.GETLENGTH(v_ddl) / 32767) LOOP\n         DBMS_LOB.READ(v_ddl, LEAST(32767, DBMS_LOB.GETLENGTH(v_ddl) - (i-1)*32767), (i-1)*32767+1, v_line);\n         UTL_FILE.PUT_LINE(v_file, v_line);\n       END LOOP;\n       \n       UTL_FILE.FCLOSE(v_file);\n     END LOOP;\n   EXCEPTION\n     WHEN OTHERS THEN\n       IF UTL_FILE.IS_OPEN(v_file) THEN UTL_FILE.FCLOSE(v_file); END IF;\n       RAISE;\n   END;