DECLARE @UserIds TABLE(UserID INT);\n\nINSERT INTO @UserIds (UserID)\nVALUES (1), (2), (3); -- Example user IDs\n\nSELECT w.*\nFROM Workplan w\nINNER JOIN (\n    SELECT UserID, MAX(CreatedDate) AS LatestCreatedDate\n    FROM Workplan\n    WHERE UserID IN (SELECT UserID FROM @UserIds)\n    GROUP BY UserID\n) latest ON w.UserID = latest.UserID AND w.CreatedDate = latest.LatestCreatedDate;