SELECT \n  CASE \n    WHEN LENGTH(message) - LENGTH(REPLACE(message, '(', '')) = 2 THEN \n      SUBSTRING_INDEX(SUBSTRING_INDEX(message, ')', -1), '(', -1)\n    ELSE \n      NULL\n  END AS Column1,\n  CASE \n    WHEN LENGTH(message) - LENGTH(REPLACE(message, '(', '')) = 2 THEN \n      TRIM(BOTH '(' AND ')' FROM SUBSTRING_INDEX(SUBSTRING_INDEX(message, ')', 1), '(', -1))\n    ELSE \n      SUBSTRING_INDEX(message, ')', -1)\n  END AS Column2\nFROM your_table;