SELECT \n    @row_number:=@row_number + 1 AS position,\n    p.*\nFROM \n    photos p, \n    (SELECT @row_number:=0) r\nORDER BY \n    p.order_column;\n\n-- To find the position of a specific photo with a given id:\nSELECT f.position\nFROM (\n    SELECT \n        @row_number:=@row_number + 1 AS position,\n        p.*\n    FROM \n        photos p, \n        (SELECT @row_number:=0) r\n    ORDER BY \n        p.order_column\n) f\nWHERE f.id = your_photo_id;