SELECT \n    it.TicketID,\n    ep1.EmployeeFirstName + ' ' + ep1.EmployeeLastName AS TicketRequestorFullName,\n    ep2.EmployeeFirstName + ' ' + ep2.EmployeeLastName AS ApprovalManagerFullName,\n    it.RequestDate, \n    it.ApprovalDate, \n    it.TicketStatus\nFROM    \n    tblIssueTicket it\nJOIN \n    tblEmployeeProfile ep1 ON ep1.EmployeeID = it.TicketRequesterID\nLEFT JOIN \n    tblEmployeeProfile ep2 ON ep2.EmployeeID = it.ApprovalManagerID;