DECLARE @current_id INT;\nDECLARE @created_in_variant INT;\n\nDECLARE cur CURSOR FOR \nSELECT id FROM alerts;\n\nOPEN cur;\nFETCH NEXT FROM cur INTO @current_id;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC find_root @id = @current_id, @variant_output = @created_in_variant OUTPUT;\n    \n    UPDATE alerts\n    SET created_in_variant = @created_in_variant\n    WHERE id = @current_id;\n    \n    FETCH NEXT FROM cur INTO @current_id;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;