WITH Months AS (\n    SELECT 1 AS month_number UNION ALL\n    SELECT 2 UNION ALL\n    SELECT 3 UNION ALL\n    SELECT 4 UNION ALL\n    SELECT 5 UNION ALL\n    SELECT 6 UNION ALL\n    SELECT 7 UNION ALL\n    SELECT 8 UNION ALL\n    SELECT 9 UNION ALL\n    SELECT 10 UNION ALL\n    SELECT 11 UNION ALL\n    SELECT 12\n),\nItemMonths AS (\n    SELECT i.itemID, i.itemName, m.month_number\n    FROM t_item i\n    CROSS JOIN Months m\n)\nSELECT \n    im.itemName,\n    im.month_number AS Month,\n    ISNULL(SUM(v.value), 0) AS total_value\nFROM ItemMonths im\nLEFT JOIN t_value v ON im.itemID = v.itemID AND MONTH(v.date) = im.month_number\nGROUP BY im.itemName, im.month_number\nORDER BY im.itemName, im.month_number;