CREATE TABLE data (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(255),\n    dept VARCHAR(100),  -- This could be a foreign key eventually pointing to lookup_values with column_name = 'dept'\n    age INT,\n    salary DECIMAL(15, 2)\n);\n\nCREATE TABLE lookup_values (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    column_name VARCHAR(100) NOT NULL,\n    value VARCHAR(255) NOT NULL,\n    description TEXT\n);