-- Main Results Table (could also contain common fields like test_id, patient_id, timestamp)\nCREATE TABLE RESULTS (\n    result_id NUMBER PRIMARY KEY,\n    test_id   NUMBER,\n    patient_id NUMBER,\n    entry_date TIMESTAMP\n);\n\n-- Child Table for Whole Number Counts\nCREATE TABLE COUNT_RESULTS (\n    count_result_id NUMBER PRIMARY KEY,\n    raw_count     NUMBER(10), -- Assuming max value is 1000\n    result_id     NUMBER UNIQUE,\n    FOREIGN KEY (result_id) REFERENCES RESULTS(result_id)\n);\n\n-- Child Table for Decimal Numbers with High Precision\nCREATE TABLE DECIMAL_RESULTS (\n    decimal_result_id NUMBER PRIMARY KEY,\n    raw_decimal     NUMBER(20,6), -- Total digits: 20, Decimal places: 6\n    result_id       NUMBER UNIQUE,\n    FOREIGN KEY (result_id) REFERENCES RESULTS(result_id)\n);\n\n-- Additional child tables for other data types can be created as necessary.