DECLARE @TableName NVARCHAR(128),\n        @FieldName NVARCHAR(128),\n        @SQL NVARCHAR(MAX),\n        @ArrayOfTablesObjects TABLE (TableName NVARCHAR(128), FieldName NVARCHAR(128));\n\n-- Insert your table and field names into the table variable\nINSERT INTO @ArrayOfTablesObjects VALUES ('tablename1', 'fieldname1'), ('tablename2', 'fieldname2');\n\nDECLARE TableCursor CURSOR FOR\nSELECT TableName, FieldName\nFROM @ArrayOfTablesObjects;\n\nOPEN TableCursor;\nFETCH NEXT FROM TableCursor INTO @TableName, @FieldName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = N'UPDATE [' + QUOTENAME(@TableName) + '] \n                  SET [' + QUOTENAME(@FieldName) + '] = ''xyz'' \n                  WHERE [' + QUOTENAME(@FieldName) + '] = ''123''';\n\n    -- Execute the dynamic SQL\n    EXEC sp_executesql @SQL;\n\n    FETCH NEXT FROM TableCursor INTO @TableName, @FieldName;\nEND;\n\nCLOSE TableCursor;\nDEALLOCATE TableCursor;