DECLARE @script NVARCHAR(MAX)\nSET @script = N'\n-- Your SQL Script goes here\nSELECT ''Running on Database: '' + DB_NAME()\n-- Example query, replace with actual logic\nSELECT * FROM SomeTable;\n';\n\nDECLARE @dbname sysname\nDECLARE db_cursor CURSOR FOR \nSELECT name \nFROM sys.databases \nWHERE state_desc = 'ONLINE' -- Optional condition to filter online databases only\n\nOPEN db_cursor  \nFETCH NEXT FROM db_cursor INTO @dbname  \n\nWHILE @@FETCH_STATUS = 0  \nBEGIN  \n    EXEC('USE [' + QUOTENAME(@dbname) + '] ' + @script)\n    FETCH NEXT FROM db_cursor INTO @dbname  \nEND \n\nCLOSE db_cursor  \nDEALLOCATE db_cursor