-- Create a trigger on Mammals table that checks if the AnimalType is 1 (Mammal)\nCREATE TRIGGER trg_CheckMammalsBeforeInsert\nON Mammals\nINSTEAD OF INSERT\nAS\nBEGIN\n    -- Check if all inserted rows have an AnimalType of 1 in Animals table\n    IF EXISTS (\n        SELECT 1\n        FROM inserted i\n        LEFT JOIN Animals a ON i.AnimalId = a.AnimalId\n        WHERE a.AnimalType <> 1 OR a.AnimalType IS NULL\n    )\n    BEGIN\n        RAISERROR('Only mammals are allowed in the Mammals table.', 16, 1);\n        ROLLBACK TRANSACTION;\n        RETURN;\n    END\n\n    -- If all rows pass the check, insert them into the Mammals table\n    INSERT INTO Mammals (AnimalId, NumberOfMammaryGlads)\n    SELECT AnimalId, NumberOfMammaryGlads FROM inserted;\nEND;