ALTER FUNCTION [dbo].[ActualWeightDIMS]\n(\n    -- Add the parameters for the function here\n    @ActualWeight int,\n    @Actual_Dims_Lenght int,\n    @Actual_Dims_Width int,\n    @Actual_Dims_Height int\n)\nRETURNS varchar(50)\nAS\nBEGIN\n\nDECLARE @ActualWeightDIMS varchar(50);\n\n-- Check if Actual Weight is not null and set it as the result\nIF (@ActualWeight IS NOT NULL) \n     SET @ActualWeightDIMS = CAST(@ActualWeight AS VARCHAR);\nELSE -- If Actual Weight is null, check dimensions and concatenate them\n    IF (@Actual_Dims_Lenght IS NOT NULL AND \n        @Actual_Dims_Width IS NOT NULL AND \n        @Actual_Dims_Height IS NOT NULL)\n          SET @ActualWeightDIMS = \n              CAST(@Actual_Dims_Lenght AS VARCHAR) + 'x' +\n              CAST(@Actual_Dims_Width AS VARCHAR) + 'x' +\n              CAST(@Actual_Dims_Height AS VARCHAR);\n\nRETURN(@ActualWeightDIMS);\n\nEND