-- Products table with basic product information\nCREATE TABLE products (\n  id INT(11) NOT NULL AUTO_INCREMENT,\n  product_name VARCHAR(255) DEFAULT NULL,\n  product_description TEXT,\n  PRIMARY KEY (id)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\n\n-- Attributes table for storing all possible attributes that products can have\nCREATE TABLE attributes (\n  attribute_id INT(11) NOT NULL AUTO_INCREMENT,\n  attribute_name VARCHAR(255) NOT NULL UNIQUE,\n  data_type ENUM('int', 'varchar', 'text', 'enum', 'float') DEFAULT 'varchar',\n  PRIMARY KEY (attribute_id)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\n\n-- Product_attributes table for storing the relationship between products and their attributes\nCREATE TABLE product_attributes (\n  product_id INT(11),\n  attribute_id INT(11),\n  value TEXT,\n  PRIMARY KEY (product_id, attribute_id),\n  FOREIGN KEY (product_id) REFERENCES products(id),\n  FOREIGN KEY (attribute_id) REFERENCES attributes(attribute_id)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\n\n-- Example of inserting data\nINSERT INTO attributes (attribute_name, data_type) VALUES ('screen_size', 'varchar');\nINSERT INTO attributes (attribute_name, data_type) VALUES ('flavor', 'varchar');\n\nINSERT INTO products (product_name, product_description) VALUES ('17" LCD Monitor', 'A standard lcd monitor');\nINSERT INTO products (product_name, product_description) VALUES ('Chocolate Ice Cream', 'Delicious chocolate ice cream');\n\nINSERT INTO product_attributes (product_id, attribute_id, value) \nSELECT p.id, a.attribute_id, '17"' FROM products AS p JOIN attributes AS a WHERE p.product_name = '17" LCD Monitor' AND a.attribute_name = 'screen_size';\n\nINSERT INTO product_attributes (product_id, attribute_id, value) \nSELECT p.id, a.attribute_id, 'Chocolate' FROM products AS p JOIN attributes AS a WHERE p.product_name = 'Chocolate Ice Cream' AND a.attribute_name = 'flavor';