ALTER FUNCTION uf_AlphaNumericIncrement\n(\n    @ID varchar(10)\n)\nRETURNS VARCHAR(10) AS\nBEGIN\n    DECLARE @AlphaPart varchar(10),\n            @NumPart varchar(10),\n            @StartIntPos int,\n            @NewNumValue int;\n    \n    -- Find the position where numeric part starts\n    SET @StartIntPos = PATINDEX('%[0-9]%', @ID);\n\n    IF @StartIntPos > 0 \n    BEGIN\n        -- Extract alphabetic part and numeric part\n        SET @AlphaPart = LEFT(@ID, @StartIntPos - 1);\n        SET @NumPart = SUBSTRING(@ID, @StartIntPos, LEN(@ID) - @StartIntPos + 1);\n\n        -- Convert numeric part to integer, increment it by 1, and convert back to varchar\n        SET @NewNumValue = CAST(@NumPart AS int) + 1;\n        \n        SET @RES = @AlphaPart + RIGHT(REPLICATE('0', LEN(@NumPart)) + CAST(@NewNumValue AS varchar), LEN(@NumPart));\n    END\n    ELSE \n    BEGIN\n        -- If no numeric part is found, just return the original value or handle it as needed.\n        SET @RES = @ID;\n    END\n    \n    RETURN @RES;\nEND