DECLARE @user_id INT;\n\n-- Declare the cursor to iterate over the user_ids\nDECLARE userCursor CURSOR FOR\nSELECT user_id FROM your_table WHERE some_condition = true;\n\n-- Open the cursor\nOPEN userCursor;\n\n-- Fetch the first row into the variable\nFETCH NEXT FROM userCursor INTO @user_id;\n\n-- Loop while there are rows to fetch\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Execute the stored procedure with the current user_id\n    EXEC your_stored_procedure @user_id = @user_id;\n\n    -- Fetch the next row\n    FETCH NEXT FROM userCursor INTO @user_id;\nEND\n\n-- Clean up: close and deallocate the cursor\nCLOSE userCursor;\nDEALLOCATE userCursor;