-- Table to store current user data\nCREATE TABLE users (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    -- other fields...\n);\n\n-- Table to store history of changes\nCREATE TABLE audit_trail (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    user_id INT,\n    field_name VARCHAR(100),\n    old_value TEXT,\n    new_value TEXT,\n    changed_by VARCHAR(255), -- Username of the person who made the change\n    changed_from_ip VARCHAR(45), -- IP address from which the change was made\n    changed_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Example of how to insert an audit log entry when a user updates their information\nINSERT INTO audit_trail (user_id, field_name, old_value, new_value, changed_by, changed_from_ip)\nVALUES (:user_id, 'name', :old_name, :new_name, :changed_by_username, :changed_from_ip);