-- Table for the superclass GradStudent\nCREATE TABLE GradStudent (\n    studentID INT PRIMARY KEY,\n    firstName VARCHAR(50),\n    lastName VARCHAR(50),\n    birthDate DATE\n);\n\n-- Table for TeachAsst, inherits from GradStudent\nCREATE TABLE TeachAsst (\n    studentID INT PRIMARY KEY,\n    courseAssignment VARCHAR(100),\n    FOREIGN KEY (studentID) REFERENCES GradStudent(studentID)\n);\n\n-- Table for ResearchAsst, inherits from GradStudent\nCREATE TABLE ResearchAsst (\n    studentID INT PRIMARY KEY,\n    researchGrant DECIMAL(10, 2),\n    FOREIGN KEY (studentID) REFERENCES GradStudent(studentID)\n);