-- Create a table for storing user disk usage\nCREATE TABLE user_disk_usage (\n    report_id SERIAL PRIMARY KEY,\n    report_date DATE NOT NULL,\n    user_id TEXT NOT NULL,\n    disk_space_used BIGINT NOT NULL -- in bytes or appropriate units\n);\n\n-- Create a table for storing average memory usage per month\nCREATE TABLE avg_memory_usage (\n    report_id SERIAL PRIMARY KEY,\n    report_month DATE NOT NULL CHECK (report_month = date_trunc('month', report_month)),\n    user_id TEXT NOT NULL,\n    avg_memory_percent FLOAT NOT NULL -- as a percentage\n);\n\n-- Create a table for storing average CPU time usage per month\nCREATE TABLE avg_cpu_time_usage (\n    report_id SERIAL PRIMARY KEY,\n    report_month DATE NOT NULL CHECK (report_month = date_trunc('month', report_month)),\n    user_id TEXT NOT NULL,\n    avg_cpu_seconds FLOAT NOT NULL -- in seconds or appropriate units\n);\n\n-- Create indexes on frequently queried columns to improve performance\nCREATE INDEX idx_user_disk_usage ON user_disk_usage(report_date, user_id);\nCREATE INDEX idx_avg_memory_usage ON avg_memory_usage(report_month, user_id);\nCREATE INDEX idx_avg_cpu_time_usage ON avg_cpu_time_usage(report_month, user_id);