-- Create a new Clients table with a primary key\nCREATE TABLE NewClients (\n    ClientID INT IDENTITY(1, 1) PRIMARY KEY,\n    ClientName NVARCHAR(255) UNIQUE NOT NULL\n);\n\n-- Populate the NewClients table with distinct client names from Projects\nINSERT INTO NewClients (ClientName)\nSELECT DISTINCT ClientName FROM Projects WHERE ClientName IS NOT NULL;\n\n-- Add a foreign key in Projects to reference the NewClients table\nALTER TABLE Projects ADD ClientID INT;\nUPDATE Projects SET ClientID = NC.ClientID\nFROM Projects P\nJOIN NewClients NC ON NC.ClientName = P.ClientName;\n\nALTER TABLE Projects\nADD CONSTRAINT FK_Projects_NewClients FOREIGN KEY (ClientID) REFERENCES NewClients(ClientID);