-- Main table with GUID as primary key\nCREATE TABLE Category (\n    CategoryGUID UNIQUEIDENTIFIER PRIMARY KEY,\n    CategoryName NVARCHAR(255),\n    -- other columns...\n);\n\n-- Mapping table for NSM\nCREATE TABLE CategoryNSM (\n    CategoryID INT IDENTITY(1,1) PRIMARY KEY,\n    CategoryGUID UNIQUEIDENTIFIER REFERENCES Category(CategoryGUID),\n    LeftValue INT NOT NULL,\n    RightValue INT NOT NULL\n);