CREATE OR REPLACE PACKAGE eng_metric \nIS \n  PROCEDURE convert(degree_fahrenheit  IN OUT NUMBER, degree_celsius  IN OUT NUMBER, measure  IN VARCHAR2); \n\n  PROCEDURE convert(liters  IN OUT NUMBER, gallons  IN OUT NUMBER); \nEND eng_metric; \n/\n\nCREATE OR REPLACE PACKAGE BODY eng_metric \nAS \n  PROCEDURE Convert \n       (degree_fahrenheit  IN OUT NUMBER, \n        degree_celsius     IN OUT NUMBER, \n        measure            IN VARCHAR2) \n  IS \n    convertf   NUMBER; \n  BEGIN \n    IF measure = 'TEMP' THEN \n      IF degree_celsius IS NULL THEN \n        convertf := (degree_fahrenheit - 32) * 5 / 9; -- Corrected formula\n        degree_celsius := convertf; \n        dbms_output.Put_line('The temperature in Celsius is ' || To_char(degree_celsius)); \n      ELSIF degree_fahrenheit IS NULL THEN \n        convertf := (degree_celsius * 9 / 5) + 32; -- Corrected formula\n        degree_fahrenheit := convertf; \n        dbms_output.Put_line('The temperature in Fahrenheit is ' || To_char(degree_fahrenheit)); \n      ELSE \n        dbms_output.Put_line('Both values were provided. Please provide only one value to be converted.'); \n      END IF; \n    ELSE \n      dbms_output.Put_line('Invalid measure'); \n    END IF; \n  END convert; \n\n  PROCEDURE Convert \n       (liters   IN OUT NUMBER, \n        gallons  IN OUT NUMBER) \n  IS \n    convertlg  NUMBER; \n  BEGIN \n    IF gallons IS NULL THEN \n      convertlg := liters / 3.78541; -- Corrected formula\n      gallons := convertlg; \n    ELSIF liters IS NULL THEN \n      convertlg := gallons * 3.78541; -- Corrected formula\n      liters := convertlg; \n    ELSE \n      dbms_output.Put_line('Both values were provided. Please provide only one value to be converted.'); \n    END IF; \n  END convert; \nEND eng_metric; \n/\n\nDECLARE \n  liters   NUMBER := NULL;\n  gallons  NUMBER := 41;\nBEGIN \n  eng_metric.Convert(liters, gallons);\n  dbms_output.Put_line('Liters: ' || To_char(liters) || ', Gallons: ' || To_char(gallons));\nEND; \n/