-- Add isActive column\nALTER TABLE your_table ADD COLUMN isActive BOOLEAN DEFAULT TRUE;\n\n-- Modify a query to respect the isActive status\nSELECT * FROM your_table WHERE isActive = TRUE;\n\n-- Update procedure to check isActive status\nCREATE OR REPLACE PROCEDURE update_your_table(\n    p_id INT,\n    p_new_value VARCHAR(255)\n) LANGUAGE plpgsql AS $$\nBEGIN\n    UPDATE your_table SET some_column = p_new_value\n    WHERE id = p_id AND isActive = TRUE;\nEND $$;\n\n-- Soft-deleting a record\nUPDATE your_table SET isActive = FALSE WHERE id = 123;