SELECT \n    CONSTRAINT_NAME, \n    TABLE_NAME\nFROM \n    ALL_CONSTRAINTS\nWHERE \n    R_CONSTRAINT_NAME = (\n        SELECT \n            CONSTRAINT_NAME\n        FROM \n            ALL_CONSTRAINTS\n        WHERE \n            TABLE_NAME = 'YOUR_TABLE_NAME' AND CONSTRAINT_TYPE = 'P'\n    )\n    AND CONSTRAINT_TYPE = 'R';