WITH ComputedTimes AS (\n    SELECT \n        shecdule.*,\n        user.timezone,\n        CONVERT_TZ(\n            IF(shecdule.timestamp_start > shecdule.last_update, shecdule.timestamp_start, shecdule.last_update), \n            'GMT', \n            user.timezone\n        ) as time_usr_tz\n    FROM \n        shecdule\n        INNER JOIN user ON shecdule.user_id = user.user_id\n)\nSELECT *\nFROM ComputedTimes\nWHERE time_usr_tz < CURRENT_TIMESTAMP();