DECLARE\n  start_date DATE := TO_DATE('6/1/2009', 'MM/DD/YYYY');\n  end_date DATE := TO_DATE('6/3/2009', 'MM/DD/YYYY'); -- Adjust as needed to cover all necessary dates\n  current_date DATE := start_date;\nBEGIN\n  FOR opt IN (SELECT DISTINCT option FROM your_table) LOOP\n    WHILE current_date <= end_date LOOP\n      SELECT COUNT(*)\n        INTO some_count_variable\n        FROM your_table t\n       WHERE t.option = opt.option\n         AND t.start_date <= current_date\n         AND t.end_date >= current_date;\n      \n      -- Insert the result into a summary table or output it as needed\n      -- Example: INSERT INTO summary_table (date, option, count) VALUES (current_date, opt.option, some_count_variable);\n      \n      DBMS_OUTPUT.PUT_LINE(TO_CHAR(current_date, 'MM/DD/YYYY') || '   ' || opt.option || '      ' || some_count_variable);\n      \n      current_date := current_date + 1;\n    END LOOP;\n    \n    -- Reset date after each option processing\n    current_date := start_date;\n  END LOOP;\nEND;\n/