MERGE timesheet AS target\nUSING (SELECT '2023-10-01 08:00:00' AS datebeginning, 123 AS jobid, '2023-10-01 17:00:00' AS dateending) AS source\nON target.jobid = source.jobid\nWHEN MATCHED THEN \n    UPDATE SET target.dateending = source.dateending\nWHEN NOT MATCHED THEN    \n    INSERT (datebeginning, jobid, dateending)\n    VALUES (source.datebeginning, source.jobid, source.dateending);