SELECT \n    TC.CONSTRAINT_TYPE,\n    TC.CONSTRAINT_NAME,\n    KU.COLUMN_NAME\nFROM \n    INFORMATION_SCHEMA.TABLE_CONSTRAINTS AS TC\nINNER JOIN \n    INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KU\nON \n    TC.CONSTRAINT_SCHEMA = KU.CONSTRAINT_SCHEMA AND\n    TC.CONSTRAINT_NAME = KU.CONSTRAINT_NAME\nWHERE \n    TC.TABLE_SCHEMA = 'dbo' -- Assuming the table is in dbo schema\n    AND TC.TABLE_NAME = 'YourTableName'; -- Replace with your table name