SELECT\n    SUM(CASE WHEN name = 'test' AND $P{oneDayAgo} <= datetime AND datetime <= $P{now} THEN count ELSE 0 END) AS today,\n    SUM(CASE WHEN name = 'test' AND $P{oneWeekAgo} <= datetime AND datetime <= $P{now} THEN count ELSE 0 END) AS thisWeek,\n    SUM(CASE WHEN name = 'test' AND $P{oneMonthAgo} <= datetime AND datetime <= $P{now} THEN count ELSE 0 END) AS thisMonth\nFROM\n    statistics;