-- Table for posts\nCREATE TABLE post (\n    id          SERIAL PRIMARY KEY,\n    content     TEXT         NOT NULL,\n    ownerId     INT          NOT NULL,\n    date        TIMESTAMP    NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    notesId     INT\n);\n\n-- Table for tags\nCREATE TABLE tag (\n    id          SERIAL PRIMARY KEY,\n    name        VARCHAR(255) NOT NULL UNIQUE,\n    notesId     INT\n);\n\n-- Association table between posts and tags, including owner and association date\nCREATE TABLE tag_assoc (\n    ownerId     INT         NOT NULL,\n    tagId       INT         NOT NULL,\n    postId      INT         NOT NULL,\n    assocDate   TIMESTAMP   NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    PRIMARY KEY (ownerId, tagId, postId),\n    FOREIGN KEY (tagId) REFERENCES tag(id),\n    FOREIGN KEY (postId) REFERENCES post(id)\n);\n\n-- Indexes for efficient querying\nCREATE INDEX idx_tag_name ON tag(name);\nCREATE INDEX idx_tag_assoc_tid_pid ON tag_assoc(tagId, postId);