DECLARE @insert NVARCHAR(MAX);\n-- Ensure the table name is valid\nDECLARE @validTables TABLE (TableName NVARCHAR(128));\nINSERT INTO @validTables (TableName) VALUES ('Table1'), ('Table2'), ('Table3'); -- Add valid table names\n\nIF NOT EXISTS (SELECT 1 FROM @validTables WHERE TableName = @table)\nBEGIN\n    RAISERROR('Invalid table name provided.', 16, 1);\n    RETURN;\nEND\n\n-- Sanitize the message by escaping single quotes for SQL injection\nDECLARE @safeMessage NVARCHAR(MAX) = REPLACE(@message, '''', '''''');\n\n-- Construct and execute the query\nSELECT @insert = 'INSERT INTO [dbo].[' + QUOTENAME(@table) + '] (Column1) VALUES (N''' + @safeMessage + ''')';\nEXEC sp_executesql @stmt = @insert;