CREATE TABLE Product (\n    product_id CHAR(36) PRIMARY KEY DEFAULT (UUID()),\n    -- other columns\n);\n\nCREATE TABLE ProductAttribute (\n    attribute_id CHAR(36) PRIMARY KEY DEFAULT (UUID()),\n    product_id CHAR(36),\n    name VARCHAR(255),\n    value VARCHAR(255),\n    FOREIGN KEY (product_id) REFERENCES Product(product_id)\n);\n\nCREATE TABLE OrderProduct (\n    order_product_id CHAR(36) PRIMARY KEY DEFAULT (UUID()),\n    product_id CHAR(36),\n    order_id CHAR(36),\n    FOREIGN KEY (product_id) REFERENCES Product(product_id),\n    -- other columns\n);\n\nCREATE TABLE OrderProductAttribute (\n    order_product_attribute_id CHAR(36) PRIMARY KEY DEFAULT (UUID()),\n    order_product_id CHAR(36),\n    attribute_id CHAR(36),\n    value VARCHAR(255),\n    FOREIGN KEY (order_product_id) REFERENCES OrderProduct(order_product_id),\n    FOREIGN KEY (attribute_id) REFERENCES ProductAttribute(attribute_id)\n);