DECLARE\n    -- Define a record type for your table structure\n    TYPE my_record_type IS RECORD (\n        id NUMBER,\n        name VARCHAR2(100)\n    );\n\n    -- Define a collection type that uses the record type\n    TYPE my_table_type IS TABLE OF my_record_type;\n\n    -- Declare a variable to hold your data\n    v_data my_table_type := my_table_type();\n\nBEGIN\n    -- Fetch the rows into the collection, example for a table named `my_queue`\n    SELECT id, name BULK COLLECT INTO v_data FROM my_queue WHERE condition = 'some_value';\n\n    -- Do something with your data here, like returning it to a caller if needed\n\n    -- Delete the fetched rows\n    FORALL i IN 1 .. v_data.COUNT\n        DELETE FROM my_queue WHERE id = v_data(i).id;\n\n    COMMIT; -- Commit the changes if necessary\nEND;\n/