WITH AbsenceSequences AS (\n    SELECT \n        EmployeeNumber, \n        EntryDate, \n        Status,\n        -- Create a sequence number for status changes\n        ROW_NUMBER() OVER (PARTITION BY EmployeeNumber ORDER BY EntryDate) -\n        ROW_NUMBER() OVER (PARTITION BY EmployeeNumber, Status ORDER BY EntryDate) AS SequenceGroup\n    FROM [Attendance]\n    WHERE Status = 'A'\n),\nSequenceCounts AS (\n    SELECT \n        EmployeeNumber,\n        COUNT(EntryDate) AS AbsentDays\n    FROM AbsenceSequences\n    GROUP BY EmployeeNumber, SequenceGroup\n)\nSELECT \n    EmployeeNumber\nFROM SequenceCounts\nWHERE AbsentDays >= n;  -- Replace n with the number of days you are looking for