CREATE FUNCTION dbo.GetDynamicTableData(@TableName NVARCHAR(128))\nRETURNS @ResultTable TABLE (Column1 INT, Column2 VARCHAR(100)) -- Define your column structure here\nAS\nBEGIN\n    IF @TableName = 'TableA'\n        INSERT INTO @ResultTable\n        SELECT Column1, Column2 FROM [dbo].[TableA];\n\n    ELSE IF @TableName = 'TableB'\n        INSERT INTO @ResultTable\n        SELECT Column1, Column2 FROM [dbo].[TableB];\n\n    -- Add more conditions as needed...\n\n    RETURN;\nEND;