USE DB1;\n   GO\n\n   CREATE TRIGGER trg_UpdateDB2Table\n   ON SourceTable\n   AFTER INSERT, UPDATE, DELETE\n   AS\n   BEGIN\n      SET NOCOUNT ON;\n\n      -- Insert or update in DB2 based on changes detected in DB1.SourceTable\n      IF EXISTS(SELECT * FROM inserted)\n         INSERT INTO DB2.dbo.TargetTable (Column1, Column2)\n         SELECT i.Column1, i.Column2 \n         FROM inserted i \n         LEFT JOIN deleted d ON i.PrimaryKey = d.PrimaryKey\n         WHERE d.PrimaryKey IS NULL; -- Condition for insert\n\n      IF EXISTS(SELECT * FROM deleted)\n         DELETE t\n         FROM DB2.dbo.TargetTable t\n         INNER JOIN deleted d ON t.PrimaryKey = d.PrimaryKey;\n\n      -- Update in DB2 based on changes detected in DB1.SourceTable\n      UPDATE t\n      SET t.Column1 = i.Column1, t.Column2 = i.Column2\n      FROM DB2.dbo.TargetTable t\n      INNER JOIN inserted i ON t.PrimaryKey = i.PrimaryKey\n      WHERE EXISTS(SELECT * FROM deleted d WHERE d.PrimaryKey = i.PrimaryKey);\n   END;