SELECT Acct_id, Name \nFROM MasterList\nWHERE NOT EXISTS (\n    SELECT 1 \n    FROM NewMasterList \n    WHERE MasterList.Acct_id = NewMasterList.Acct_id AND \n          MasterList.Name = NewMasterList.Name\n)\nUNION\nSELECT Acct_id, Name \nFROM NewMasterList\nWHERE NOT EXISTS (\n    SELECT 1 \n    FROM MasterList \n    WHERE NewMasterList.Acct_id = MasterList.Acct_id AND \n          NewMasterList.Name = MasterList.Name\n);