WITH PredefinedOrder AS (\n    SELECT val, ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS rn\n    FROM (\n        VALUES ('val1'), ('val3'), ('val5'), ('val2'), ('val4')\n    ) AS T(val)\n),\nMainQuery AS (\n    SELECT col1, col2, your_column_to_sort_by\n    FROM your_table\n)\nSELECT *\nFROM MainQuery\nORDER BY (\n    SELECT rn\n    FROM PredefinedOrder\n    WHERE PredefinedOrder.val = MainQuery.your_column_to_sort_by\n);