DECLARE @text NVARCHAR(MAX) = N'The quick brown fox jumped over the lazy dog.  Then he got bored and went to bed.  After that, he played with his friends.\r\nThe next day, he and his friends had a big party.'\n\nSELECT REPLACE(\n    REPLACE(@text, CHAR(13) + CHAR(10), '</p><p>'),\n    '\n', ''\n) AS FormattedHTML;\n\n-- To wrap the entire text in <p> tags\nSELECT '<p>' + REPLACE(\n    REPLACE(@text, CHAR(13) + CHAR(10), '</p><p>'),\n    '\n', ''\n) + '</p>' AS FormattedHTML;