-- Declare a table variable to hold the results of sp_columns\nDECLARE @Columns TABLE (\n    TABLE_QUALIFIER NVARCHAR(128),\n    TABLE_OWNER NVARCHAR(128),\n    TABLE_NAME NVARCHAR(257),\n    COLUMN_NAME NVARCHAR(128),\n    DATA_TYPE SMALLINT,\n    TYPE_NAME NVARCHAR(257),\n    PRECISION INT,\n    LENGTH BIGINT,\n    SCALE TINYINT,\n    RADIX SMALLINT,\n    NULLABLE SMALLINT,\n    REMARKS NVARCHAR(254),\n    COLUMN_DEF NVARCHAR(17)\n);\n\n-- Insert the results of sp_columns into the table variable\nINSERT INTO @Columns\nEXEC sp_columns 'tblSomeTableName';\n\n-- Select just the COLUMN_NAME from the table variable\nSELECT COLUMN_NAME FROM @Columns;