DECLARE @TenantId INT;\nSET @TenantId = 1; -- Set your tenant ID here\n\n-- Create a temporary table to hold the result set of forms\nCREATE TABLE #FormsToDelete (FormId INT);\n\n-- Insert the FormIds for the given Tenant into the temp table\nINSERT INTO #FormsToDelete (FormId)\nSELECT formId \nFROM Forms \nWHERE Tenant = @TenantId;\n\n-- Declare variables for controlling the loop\nDECLARE @CurrentFormId INT;\nWHILE EXISTS (SELECT 1 FROM #FormsToDelete)\nBEGIN\n    -- Select the top FormId from the temp table\n    SELECT TOP 1 @CurrentFormId = FormId\n    FROM #FormsToDelete;\n\n    -- Execute your stored procedure for each form ID\n    EXEC Delete_Form @FormId = @CurrentFormId;\n\n    -- Remove the processed FormId from the temp table\n    DELETE FROM #FormsToDelete\n    WHERE FormId = @CurrentFormId;\nEND\n\n-- Drop the temporary table\nDROP TABLE #FormsToDelete;