SELECT \n    t1.Month,\n    t1.Orders as Current_Orders,\n    COALESCE(t2.Orders, 0) as Previous_Orders,\n    CASE \n        WHEN COALESCE(t2.Orders, 0) = 0 THEN NULL -- Avoid division by zero\n        ELSE ((t1.Orders - COALESCE(t2.Orders, 0)) * 1.0 / COALESCE(t2.Orders, 0)) * 100\n    END as Percent_Change\nFROM \n    OrdersTable t1\nLEFT JOIN \n    OrdersTable t2\nON \n    t1.Month = t2.Month + 1 -- Joining current month with the previous month\nORDER BY \n    t1.Month;