DECLARE @EmployeeLat AS FLOAT = 40.7128; -- Example latitude\nDECLARE @EmployeeLng AS FLOAT = -74.0060; -- Example longitude\nDECLARE @RadiusMiles AS FLOAT = 5;\n\n-- Bounding box calculation (approximate)\nDECLARE @EarthRadius_miles AS FLOAT = 3959;\nDECLARE @rad AS FLOAT = PI()/180.0;\nDECLARE @lat1_min AS FLOAT, @lat1_max AS FLOAT; \nDECLARE @lon1_min AS FLOAT, @lon1_max AS FLOAT;\n\nSET @lat1_min = (@EmployeeLat - @RadiusMiles/@EarthRadius_miles) / @rad;\nSET @lat1_max = (@EmployeeLat + @RadiusMiles/@EarthRadius_miles) / @rad;\nSET @lon1_min = (@EmployeeLng - @RadiusMiles/@EarthRadius_miles/COS(@EmployeeLat * @rad)) / @rad;\nSET @lon1_max = (@EmployeeLng + @RadiusMiles/@EarthRadius_miles/COS(@EmployeeLat * @rad)) / @rad;\n\nSELECT *\nFROM MediaTargets\nWHERE latitude BETWEEN @lat1_min AND @lat1_max\nAND longitude BETWEEN @lon1_min AND @lon1_max;