-- Creating tables using SQL, as would be input into Oracle Designer\nCREATE TABLE DEPARTMENTS (\n    DEPARTMENT_ID NUMBER(4) CONSTRAINT DEPT_PK PRIMARY KEY,\n    DEPARTMENT_NAME VARCHAR2(30) NOT NULL\n);\n\nCREATE TABLE EMPLOYEES (\n    EMPLOYEE_ID NUMBER(6) CONSTRAINT EMP_PK PRIMARY KEY,\n    FIRST_NAME VARCHAR2(20),\n    LAST_NAME VARCHAR2(25),\n    EMAIL VARCHAR2(25),\n    PHONE_NUMBER VARCHAR2(20),\n    HIRE_DATE DATE NOT NULL,\n    JOB_ID VARCHAR2(10),\n    SALARY NUMBER(8,2),\n    COMMISSION_PCT NUMBER(2,2),\n    MANAGER_ID NUMBER(6),\n    DEPARTMENT_ID NUMBER(4) CONSTRAINT DEPT_FK REFERENCES DEPARTMENTS(DEPARTMENT_ID)\n);