CREATE OR REPLACE TRIGGER "JOSH".TEST_UPD \nBEFORE UPDATE ON "JOSH"."TEST_TRIGGER_TABLE" \nREFERENCING OLD AS OLD NEW AS NEW \nFOR EACH ROW\nDECLARE    \n   oldval VARCHAR2(2000);   \n   newval VARCHAR2(2000);   \nBEGIN    \n   FOR col IN (SELECT column_name FROM user_tab_columns WHERE table_name = 'TEST_TRIGGER_TABLE') LOOP\n      EXECUTE IMMEDIATE \n         'BEGIN :old_value := :OLD.' || col.column_name || '; END;'\n      USING OUT oldval, OLD;\n\n      EXECUTE IMMEDIATE \n         'BEGIN :new_value := :NEW.' || col.column_name || '; END;'\n      USING OUT newval, NEW;\n      \n      -- Do something here with the old and new values\n      IF oldval != newval THEN\n          DBMS_OUTPUT.PUT_LINE('Column ' || col.column_name || ' changed: OLD=' || oldval || ', NEW=' || newval);\n      END IF;\n   END LOOP; \nEND;