DECLARE\n   blob_loc  BLOB;\n   bfile_loc BFILE;\n   dest_file UTL_FILE.FILE_TYPE;\nBEGIN\n   -- Assuming you have a table named `documents` with columns `doc_id` and `document_blob`\n   SELECT document_blob INTO blob_loc FROM documents WHERE doc_id = 1 FOR UPDATE;\n\n   bfile_loc := BFILENAME('DOCBLOB_DIR', 'output_document.doc');\n   \n   UTL_FILE.FOPEN(bfile_loc, 'w', MAX_LINESIZE);\n   DBMS_LOB.OPEN(blob_loc, DBMS_LOB.LOB_READONLY);\n   DBMS_LOB.FILECLOSE_ALL;\nEND;\n/