SELECT * \nFROM (\n    SELECT * \n    FROM data_bahf \n    WHERE param_id = 135321 \n      AND datetime >= '2009-03-11 10:30:00' \n      AND datetime <= '2009-03-11 12:00:00'\n\n    UNION\n\n    SELECT * \n    FROM data_bahf \n    WHERE param_id = 135321 \n      AND datetime < '2009-03-11 10:30:00'\n    ORDER BY datetime DESC\n    LIMIT 1\n\n    UNION\n\n    SELECT * \n    FROM data_bahf \n    WHERE param_id = 135321 \n      AND datetime > '2009-03-11 12:00:00'\n    ORDER BY datetime ASC\n    LIMIT 1\n) AS A\nORDER BY datetime;