DECLARE @text NVARCHAR(MAX), @fontindex INT, @nextFontIndex INT;\nSET @text = (SELECT textlong1 FROM exampletable WHERE <your_condition>);\n\nWHILE CHARINDEX('fnil', @text) > 0\nBEGIN\n    -- Find the index of fnil which is followed by a space and font name\n    SET @fontindex = CHARINDEX('fnil', @text);\n    \n    -- Move to the character after fnil (and skip possible spaces)\n    SET @fontindex += 4;\n    WHILE SUBSTRING(@text, @fontindex, 1) IN (' ', '\')\n        SET @fontindex += 1;\n\n    -- Find the end of the font name by looking for a semicolon\n    SET @nextFontIndex = CHARINDEX(';', @text, @fontindex);\n    \n    -- Replace the found font with Times New Roman\n    IF @nextFontIndex > 0 AND @fontindex < @nextFontIndex\n        SET @text = STUFF(@text, @fontindex, @nextFontIndex - @fontindex,\n                          'Times New Roman');\nEND\n\n-- Output or update the result\nSELECT @text AS ModifiedText;